// src/components/ChatBot.jsx
import React, { useState, useEffect } from "react";

export default function ChatBot() {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [history, setHistory] = useState([]);
  const [showHistory, setShowHistory] = useState(false);

  useEffect(() => {
    setMessages([
      {
        role: "bot",
        text: "Hello there! How can I help you today?",
        timestamp: new Date().toISOString(),
      },
    ]);
  }, []);

  useEffect(() => {
    fetch("http://localhost:8000/history")
      .then((res) => res.json())
      .then((data) => setHistory(data.history || []))
      .catch((err) => console.error("History fetch error:", err));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const userMsg = {
      role: "user",
      text: input,
      timestamp: new Date().toISOString(),
    };
    setMessages((prev) => [...prev, userMsg]);
    setInput("");
    setLoading(true);

    try {
      const res = await fetch("http://localhost:8000/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: input }),
      });
      const data = await res.json();
      const botMsg = {
        role: "bot",
        text: data.response,
        timestamp: new Date().toISOString(),
      };
      setMessages((prev) => [...prev, botMsg]);
    } catch (err) {
      setMessages((prev) => [
        ...prev,
        {
          role: "bot",
          text: "‚ö†Ô∏è Network error. Try again.",
          timestamp: new Date().toISOString(),
        },
      ]);
    }
    setLoading(false);
  };

  const clearChat = () => {
    setMessages([
      {
        role: "bot",
        text: "Hello there! How can I help you today?",
        timestamp: new Date().toISOString(),
      },
    ]);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-white p-4">
      <div className="flex justify-between items-center mb-4">
        <div>
          <h1 className="text-2xl font-bold">AI Chatbot</h1>
          <p className="text-sm text-gray-500">Powered by Gemini Pro</p>
        </div>
        <div className="space-x-2">
          <button className="btn btn-green" onClick={clearChat}>+ New Chat</button>
          <button className="btn btn-blue" onClick={() => setShowHistory(!showHistory)}>
            üïì History
          </button>
          <button className="btn btn-red" onClick={() => setMessages([])}>
            üóëÔ∏è Clear All
          </button>
        </div>
      </div>

      <div className="flex">
        {showHistory && (
          <div className="w-64 bg-white p-4 border rounded-lg mr-4">
            <h3 className="text-lg font-semibold mb-2">üïì Chat History</h3>
            {history.map((chat, i) => (
              <div
                key={i}
                onClick={() =>
                  setMessages([
                    {
                      role: "user",
                      text: chat.user,
                      timestamp: chat.timestamp,
                    },
                    {
                      role: "bot",
                      text: chat.bot,
                      timestamp: chat.timestamp,
                    },
                  ])
                }
                className="p-2 cursor-pointer hover:bg-blue-100 rounded-md transition"
              >
                üó®Ô∏è {chat.user.slice(0, 30)}...
              </div>
            ))}
          </div>
        )}

        <div className="flex-1">
          <div className="space-y-4">
            {messages.map((msg, idx) => (
              <div
                key={idx}
                className={`max-w-[75%] rounded-lg p-3 shadow-md text-sm ${
                  msg.role === "user"
                    ? "ml-auto bg-blue-500 text-white text-right"
                    : "bg-purple-100 text-gray-800"
                }`}
              >
                <p>{msg.text}</p>
                <div className="text-[10px] mt-1 text-gray-500">
                  {msg.timestamp && !isNaN(Date.parse(msg.timestamp))
                    ? new Date(msg.timestamp).toLocaleTimeString([], {
                        hour: "2-digit",
                        minute: "2-digit",
                      })
                    : "Time Unknown"}
                </div>
              </div>
            ))}
            {loading && <div className="text-sm text-gray-500">ü§ñ Typing...</div>}
          </div>

          <form onSubmit={handleSubmit} className="mt-6 flex gap-2">
            <input
              className="flex-1 p-2 border rounded-md"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type your message..."
              required
            />
            <button className="btn btn-blue" type="submit">
              üì§ Send
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
